mapper.update:
UPDATE index_jobs
SET failed=#{failed}
WHERE id = #{id}

mapper.insert:
INSERT INTO index_jobs(id,item_id,job_type,action,failed) SELECT nextval('seq_index_job_id'), id, 'OSD','UPDATE',0 FROM objects WHERE objects.id in
#{item}
ON CONFLICT DO NOTHING

INSERT INTO index_jobs(id,item_id,job_type,action,failed) SELECT nextval('seq_index_job_id'), id, 'FOLDER','UPDATE',0 FROM folders WHERE folders.id in
#{item}
ON CONFLICT DO NOTHING
